//init beaker tab
function initBeakerTabs(){

	var activeBtnClass = 'btn-active';
	var activePopupClass = 'popup-active';
	var activeClass = 'active';
	var activeAnimationClass = 'animating';
	var activeAltAnimationClass = 'animating-alt';
	var scaleActive = 'scale-in';
	var animSpeed = 400;
	var win = jQuery(window);
	var header = jQuery("#header");
	var body = jQuery('body');

	jQuery('.animated-tabs').each(function(){
		var animatedTabs = jQuery(this);
		var btnPrev = animatedTabs.find('a.btn-prev');
		var btnNext = animatedTabs.find('a.btn-next');
		var tabset = animatedTabs.find('.tabset');
		var tabHolder = animatedTabs.find('.tab-content');
		var beakerTabs = animatedTabs.find('.beaker-tabs');
		var beakerTabsets = beakerTabs.find('.bubbles-tabset');
		var beakerTabsBtns = beakerTabs.find('a');
		var beakerTabsContent = beakerTabs.find('.beaker-tab-content');
		var beakerBox = tabHolder.find('.beaker-box');
		var slider = animatedTabs.find('.tab-slider');
		var tabsetMask = animatedTabs.find('.tabset-wrap .mask');
		var tabBtnsWrap = tabset.find('li');
		var tabBtns = tabset.find('a');
		var bgBlock = tabHolder.find('.bg');
		var bubblesBlock = tabHolder.find('.bubbles-holder');
		var bubbles = bubblesBlock.find('.bubble');
		var textBlock = tabHolder.find('.add-info');
		var curBtnIndex = tabBtns.filter('.'+activeClass).parent().index();
		var curThemeClass = tabBtns.filter('.'+activeClass).data('rel');
		var animFlag = true;
		var delay = 1100;

		tabHolder.addClass(curThemeClass);

		function sliderHandler(){

			var activeBtn = tabBtnsWrap.eq(curBtnIndex);
			var leftOffset = activeBtn.offset().left - tabset.offset().left + (activeBtn.innerWidth() / 2);
			slider.animate({
				left: leftOffset
			});
			setTimeout(function(){
				animFlag = true;
			}, delay);
		}

		function themeHandler(){
			tabHolder.removeClass(curThemeClass);
			var activeBtn = tabBtnsWrap.eq(curBtnIndex).find('a');
			curThemeClass = activeBtn.data('rel');
			tabHolder.addClass(curThemeClass);
		}


		function maskScrollHandler(){
			var offsetLeft = curBtnIndex * tabBtnsWrap.width();
			tabsetMask.animate({scrollLeft: offsetLeft}, animSpeed);
		}


		btnPrev.on('click', function(e){

			e.preventDefault();


			if(animFlag){
				animFlag = false;
				if(curBtnIndex !== 0){
					curBtnIndex--;
				}
				themeHandler();
				maskScrollHandler();
				sliderHandler();
			}

			if(beakerTabsContent.hasClass(scaleActive)){
				beakerTabsContent.removeClass(scaleActive);
				beakerTabsContent.css({
					height: 0
				});
			}

			beakerTabsBtns.parent().removeClass(activeBtnClass);

		});


		btnNext.on('click', function(e){

			e.preventDefault();


			if(animFlag){
				animFlag = false;
				if(curBtnIndex !== tabBtnsWrap.length - 1){
					curBtnIndex++;
				}
				themeHandler();
				maskScrollHandler();
				sliderHandler();
			}

			if(beakerTabsContent.hasClass(scaleActive)){
				beakerTabsContent.removeClass(scaleActive);
				beakerTabsContent.css({
					height: 0
				});
			}

			beakerTabsBtns.parent().removeClass(activeBtnClass);
		});

		tabsetMask.on('scroll', sliderHandler);

		function bgHeightHandler(){

			//var bgHeight = textBlock.innerHeight() + 130;

			tabHolder.css({
				minHeight: beakerBox.innerHeight() + textBlock.innerHeight()
			});

			/*bgBlock.css({
				bottom: bgHeight
			});

			bubblesBlock.css({
				bottom: bgHeight
			});*/
		}

		sliderHandler();
		bgHeightHandler();

		win.on('load resize orientationchange', function(){
			sliderHandler();
			bgHeightHandler();
		});

			
		tabBtns.each(function(){
			var tabBtn = jQuery(this);
			
			tabBtn.on('click', function(e){
				e.preventDefault();

				if(header.length > 0){
					jQuery('html,body').animate({
						scrollTop: tabset.offset().top - header.innerHeight()
					}, animSpeed * 1.5);
				}else{
					jQuery('html,body').animate({
						scrollTop: tabset.offset().top
					}, animSpeed * 1.5);
				}
				
				body.removeClass(activePopupClass);
				tabBtns.removeClass(activeClass);
				beakerTabsets.removeClass(activeClass);
				tabBtn.addClass(activeClass);
				tabHolder.removeClass(activeClass);
				beakerTabs.removeClass(activeClass);

				if(beakerTabsContent.hasClass(scaleActive)){
					beakerTabsContent.removeClass(scaleActive);
					beakerTabsContent.css({
						height: 0
					});
				}

				beakerTabsBtns.parent().removeClass(activeBtnClass);

				curBtnIndex = tabBtn.parent().index();
				sliderHandler();
				themeHandler();
			});
		});

		beakerTabs.each(function(){
			var hold = jQuery(this);
			var bubblesTabset = hold.find('.bubbles-tabset');
			var btns = hold.find('.bubbles-tabset a');
			var tabContent = hold.find('.beaker-tab-content');
			var tabContentHeight = tabContent.innerHeight();
			var close = hold.find('.tab-close');
			var tabBlocks = hold.find('.bubble-tab');
			var fadeSpeed = 250;

			tabContent.css({
				height: 0
			});

			function animHandler(){
				bubblesBlock.removeClass(activeAnimationClass);
				setTimeout(
					function(){bubblesBlock.addClass(activeAltAnimationClass)}
				, 10);
			}

			function animAltHandler(){
				bubblesBlock.removeClass(activeAltAnimationClass);
				setTimeout(
					function(){bubblesBlock.addClass(activeAnimationClass)}
				, 10);
			}

			btns.each(function(){
				var btn = jQuery(this);
				var href = btn.attr('href');
				var curBlock = jQuery(href);
				
				btn.on('click', function(e){
					e.preventDefault();

					btns.parent().removeClass(activeBtnClass);
					btn.parent().addClass(activeBtnClass);

					if(!bubblesTabset.hasClass(activeClass)){
						bubblesTabset.addClass(activeClass);
					}

					if(!body.hasClass(activePopupClass)){
						body.addClass(activePopupClass);
					}

					animHandler();

					if(!beakerTabs.hasClass(activeClass)){
						beakerTabs.addClass(activeClass);
						tabHolder.addClass(activeClass);
						tabContent.animate({
							height: tabContentHeight
						},{
							duration: animSpeed,
							complete: function(){
								if(!tabContent.hasClass(scaleActive)){
									tabContent.addClass(scaleActive);
								}
							}
						});
					}
					tabBlocks.fadeOut(animSpeed);

					setTimeout(function() {
						curBlock.fadeIn(animSpeed);
					}, animSpeed);
					
					if(header.length > 0){
						jQuery('html,body').animate({
							scrollTop: tabHolder.offset().top - header.innerHeight() + 4
						}, 800);
					}else{
						jQuery('html,body').animate({
							scrollTop: tabHolder.offset().top
						}, 800);
					}
				});
			});
			close.on('click', function(e){
				e.preventDefault();

				animAltHandler();

				btns.parent().removeClass(activeBtnClass);
				beakerTabs.removeClass(activeClass);
				tabHolder.removeClass(activeClass);
				beakerTabsets.removeClass(activeClass);
				body.removeClass(activePopupClass);
				if(tabContent.hasClass(scaleActive)){
					tabContent.removeClass(scaleActive);
				}
				tabBlocks.hide();
				setTimeout(function() {
					tabContent.animate({
						height: 0
					},{
						duration: animSpeed
					});
				}, animSpeed);
			});
			ResponsiveHelper.addRange({
				'..767': {
					on: function() {
						bubblesBlock.addClass(activeAnimationClass);
					},
					off: function(){
						bubblesBlock.removeClass(activeAltAnimationClass);
						bubblesBlock.removeClass(activeAnimationClass);
					}
				}
			});
		});

	});
}


//init check Scroll For Presentation
function initcheckScrollForPresentation() {
	var moving_line = jQuery('.card-html-code .line'),
		front_image = jQuery('.card-html-code .front-image'),
		back_image = jQuery('.card-html-code .back-image'),
		html_code_section = jQuery('#htmlCodeSection');
	
	if (html_code_section.length) {
		html_code_section = html_code_section.offset().top - 200;
		
		jQuery(window).on('scroll', function() {
			var current_scroll = jQuery(this).scrollTop(),
				move_line_distance = (current_scroll - html_code_section) * 1.3;
			
			move_line_distance = Math.ceil(move_line_distance);
			if (current_scroll > html_code_section){
				if(move_line_distance <= 460){
					moving_line.css('left',move_line_distance);
					front_image.css('width', 460 - move_line_distance);
					back_image.css('width', move_line_distance);
				}
			}
		});
	}
}


