// slot machine effect plugin
;(function($){
	function SlotMachine(options){
		this.options = $.extend({
			animSpeed: 8000,
			delay: 2000,
			slots: 15
		},options);
		this.init();
	}
	SlotMachine.prototype = {
		init: function(){
			if (this.options.holder) {
				this.findElements();
				this.generateStructure();
				this.attachEvents();
				this.scrollHandler();
			}
		},
		findElements: function(){
			this.win = $(window);
			this.holder = $(this.options.holder);
			this.animate = false;
		},
		generateStructure: function(){
			var nums = this.holder.text().split('');
			this.holder.empty();
			for (var i = 0; i < nums.length; i++){
				var num = nums[i];
				var randomNumbers = '';
				var structure = '<span class="slot-wrapper"><span class="slot-slider">';
				if (this.isNumber(num)) {
					// insert random number
					for (var j = 0; j < this.options.slots; j++){
						var randomNum = Math.floor(Math.random() * 9);
						structure += '<span class="slot random-number">'+randomNum+'</span>';
						randomNumbers += '<span class="slot random-number">'+randomNum+'</span>';
					}
					// insert main number
					structure += '<span class="slot main-number">'+num+'</span>';
				} else {
					for (var j = 0; j < this.options.slots; j++){
						var randomNum = Math.floor(Math.random() * 9);
						structure += '<span class="slot random-number">'+num+'</span>';
						randomNumbers += '<span class="slot random-number">'+num+'</span>';
					}
					// insert separator
					// structure += '<span class="slot separator">'+num+'</span>';
				}
				structure += randomNumbers + '</span></span>';
				this.holder.append($(structure));
			}
			this.wrapper = this.holder.find('.slot-wrapper');
			this.sliders = this.holder.find('.slot-slider');
		},
		attachEvents: function(){
			var self = this;
			this.win.on('scroll resize orientationchange', function(){
				self.scrollHandler();
			});
			
		},
		scrollHandler: function(){
			if (this.win.scrollTop() < this.holder.offset().top && this.win.scrollTop() + this.win.height() >  this.holder.offset().top){
				this.moveSlots();
			} else {
				this.stopSlots();
			}
		},
		moveSlots: function(){
			var self = this;
			if (!this.animate) {
				this.animate = true;
				this.sliders.each(function(){
					var slider = $(this);
					if (slider.children().length > 1) {
						var offset = self.wrapper.eq(0).height() * self.options.slots;
						slider.animate({marginTop: -offset},{queue: false, easing: 'easeOutElastic', duration: self.options.animSpeed + Math.round(Math.random()*self.options.delay)});
					}
				});
			}
		},
		stopSlots: function(){
			this.animate = false;
			this.sliders.each(function(){
				$(this).stop().removeAttr('style');
			});
		},
		isNumber: function(n){
			return !isNaN(parseFloat(n)) && isFinite(n);
		}
	};
	$.fn.slotMachine = function(opt){
		return this.each(function(){
			$(this).data('SlotMachine', new SlotMachine($.extend(opt, {holder: this})));
		});
	};
}(jQuery));

function fixFlash() {
        jQuery('object').each(function(){
            jQuery(this).find('embed').attr('wmode','transparent');
            if(jQuery(this).find('param[name=wmode]').attr('value') != 'transparent') {
                jQuery(this).prepend('<param name="wmode" value="transparent" />');
            }
            temp_var = jQuery(this).parent().html();
            jQuery(this).parent().html(temp_var);
        });
    }

