/*
 * Ajax Load Post plugin
 */
;(function($){
	function AjaxLoad(options){
		this.options = $.extend({
			contentHolder: '.content-holder',
			loaderLink: '.hidden-loader',
			btnHolder: '.btn-holder',
			btnLoad: '.btn-load',
			contentTag: 'li',
			animSpeed: 600
		},options);
		this.init();
	}
	AjaxLoad.prototype = {
		init: function(){
			if (this.options.holder) {
				this.findElements();
				this.attachEvents();
			}
		},
		findElements: function(){
			this.holder = $(this.options.holder);
			this.contentHolder = this.holder.find(this.options.contentHolder);
			this.btnHolder = this.holder.find(this.options.btnHolder);
			this.btnLoad = this.btnHolder.find(this.options.btnLoad);
			this.busy = false;
		},
		attachEvents: function(){
			var self = this;
			var clickHandler = function(e){
				e.preventDefault();
				self.ajaxCall(this.getAttribute('href'));
			};
			this.btnLoad.on('click', clickHandler);
		},
		ajaxCall: function(target){
			var self = this;
			if (!this.busy) {
				this.busy = true;
				$.ajax({
					url: target,
					type: 'get',
					data: 'ajax=1',
					dataType: 'text',
					success: function(msg){
						var data = jQuery(msg)
						var nextLink = data.filter(self.options.loaderLink);
						var content = data.filter(self.options.contentTag);

						self.contentHolder.append(content.css({display:'none'}));
						content.slideDown(self.options.animSpeed);

						if (nextLink.length) {
							self.btnLoad.attr('href', nextLink.attr('href'));
						} else {
							self.btnHolder.slideUp(self.options.animSpeed);
						}	

						self.busy = false;

						// external callback
						self.makeCallback('onLoad', self);
					},
					error: function(){
						busy = false;
					}
				});
			}
		},
		makeCallback: function(name) {
			if(typeof this.options[name] === 'function') {
				var args = Array.prototype.slice.call(arguments);
				args.shift();
				this.options[name].apply(this, args);
			}
		}
	};
	$.fn.ajaxLoad = function(opt){
		return this.each(function(){
			$(this).data('AjaxLoad', new AjaxLoad($.extend(opt, {holder: this})));
		});
	};
}(jQuery));

/*
 * jQuery Photo Slider Plugin
 */
;(function($){
	function PhotoSlider(options) {
		this.options = $.extend({
			slider: '.slider'
		}, options);
		this.init();
	}
	PhotoSlider.prototype = {
		init: function() {
			if(this.options.holder) {
				this.findElements();
				this.attachEvents();
				this.makeCallback('onInit', this);
			}
		},
		findElements: function() {
			var self = this;
			this.body = $('body');
			this.holder = $(this.options.holder);
			this.mask = this.holder.find(this.options.mask);
			this.slider = this.holder.find(this.options.slider);
			this.hover = false;
			this.mask.css({msTouchAction: 'none'});
		},
		attachEvents: function() {
			// add handler
			var self = this;
			this.moveHandler = function(e){
				e = e.originalEvent.changedTouches ? e.originalEvent.changedTouches[0] : e;
				if (self.hover) {
					var width = Math.ceil(e.pageX - self.holder.offset().left)*100/self.mask.width() + '%';
					if (e.pageX < self.mask.offset().left){
						width = 0;
					} else if (e.pageX > self.mask.offset().left + self.mask.width()) {
						width = '100%';
					}
					self.slider.css({
						width: width
					});
				}
				e.preventDefault();
			};
			this.mask.on('mouseenter touchstart pointerdown MSPointerDown selectstart', function(e){
				self.hover = true;
				self.moveHandler(e);
				e.preventDefault();
			}).on('mouseleave touchend pointerup MSPointerUp', function(e){
				setTimeout(function(){
					self.hover = false;
				},0);
				e.preventDefault();
			});
			this.body.on('mousemove touchmove pointermove MSPointerMove', this.moveHandler);
		},
		makeCallback: function(name) {
			if(typeof this.options[name] === 'function') {
				var args = Array.prototype.slice.call(arguments);
				args.shift();
				this.options[name].apply(this, args);
			}
		}
	};

	// jQuery plugin interface
	$.fn.photoSlider = function(opt) {
		return this.each(function() {
			jQuery(this).data('PhotoSlider', new PhotoSlider($.extend(opt, {holder: this})));
		});
	};
}(jQuery));

// animation start
function initAnimationSolutions() {
	if(!jQuery('#solutions').length ) {return;}
	var holder = jQuery('#solutions');
	var holderClass = 'animate';
	var holderPos = holder.offset().top;
	var page = jQuery(window);
	var flag = false;
	function addClassToHolder () {
		var scrollPos = page.scrollTop();
		if(scrollPos >= holderPos){
			holder.addClass(holderClass);
			if(!flag){
				flag = true;
				initTabs();
			}
		} else{
			holder.removeClass(holderClass);
			if(flag){
				flag = false;
				destroyTabs();
			}
		}
	}
	addClassToHolder();
	$(document).on('scroll', function () {addClassToHolder();});
}

