// init toggle header
function initToggleHeader(){
	var animClass = 'sticky';
	var bodyHomeFixClass = 'no-text-on-video';
	var activeClass = 'active';
	var win = jQuery(window);
	var body = jQuery('body');
	var wrapper = jQuery('#wrapper');
	var header = jQuery('#header');
	var fixedBox = jQuery('.top-section').find('.video-section');
	var menu = jQuery('.sub-menu-holder.anchors');
	var cloneMenu = menu.clone().addClass('main-menu').appendTo(header);
	var menuOpener = header.find('.navbar-toggle');
	var mainContentBlock = jQuery('#main');
	var flag = true;

	var scrollHandler = function(){
		var winTop = win.scrollTop();
		var headerHeight = header.outerHeight();
		if (winTop > mainContentBlock.offset().top - headerHeight) {
			header.addClass(animClass);
			body.addClass(bodyHomeFixClass);
		} else {
			header.removeClass(animClass);
			body.removeClass(bodyHomeFixClass);
		}
		if (menu.length) {
			var menuPos = menu.offset().top;
			if(winTop >= menuPos - headerHeight){
				wrapper.addClass(activeClass);
			} else if(winTop <= menuPos + headerHeight){
				wrapper.removeClass(activeClass);
			}
		}
	};

	var clickHandler = function(){
		if(flag){
			body.addClass('menu-active');
			header.addClass('menu-opened');
			flag = false;
		}else{
			body.removeClass('menu-active');
			header.removeClass('menu-opened');
			flag = true;
		}
	};

	scrollHandler();
	menuOpener.on('click', clickHandler);
	win.on('load resize scroll orientationchange', scrollHandler);
}

