// searchbox autocomplete
function initSearchForms() {
	var activeClass = 'open';
	var hiddenClass = 'hidden';
	var items = jQuery('.category-list > li');
	jQuery('.form-search').each(function(){
		var form = jQuery(this);
		var input = form.find('#search');
		var changeHandler = function(){
			items.removeClass(hiddenClass+' '+activeClass).each(function(){
				var item = jQuery(this);
				var elements = item.find('a, p');
				var hideFlag = true;
				
				// check text
				elements.each(function(){
					var element = jQuery(this);
					if (element.text().toLowerCase().indexOf(input.val().toLowerCase()) != -1) {
						hideFlag = false;
					}
					element.html(highlightWords(element.text(), input.val()));
				})
				
				// toggle item
				if (input.val().length > 0) {
					if (hideFlag) {
						item.addClass(hiddenClass);
					} else {
						item.addClass(activeClass);
					}
					
				}
			})
			items.parents('.box').each(function(){
				var box = jQuery(this);
				var boxItems = box.find('li');
				var visibleFlag = false;
				boxItems.each(function(){
					var boxItem = jQuery(this);
					if (!boxItem.hasClass(hiddenClass)) {
						visibleFlag = true;
					}
				})
				if (visibleFlag) {
					box.removeClass(hiddenClass);
				} else {
					box.addClass(hiddenClass);
				}
			})
		}
		input.on('keyup keypress', changeHandler);
	})
	
	// regexp highlight function
	function escapeRegExp(str) {
		return str.replace(new RegExp("[.*+?|()\\[\\]{}\\\\]", "g"), "\\$&");
	}
	function highlightWords(str, word) {
		var regex = new RegExp("(" + escapeRegExp(word) + ")", "gi");
		return str.replace(regex, "<span class='highlight'>$1</span>");
	}
}

//add class for hover drop menu
function initHoverClass() {
	jQuery('ul.nav li.dropdown').each(function (){
		var itemOpener = jQuery(this).addClass('menu-item');
		var linkHref = itemOpener.find('a').attr('href');
		var isTouchDevice = /MSIE 10.*Touch/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
		var mobileFlag = false;
		if(isTouchDevice){
			itemOpener.on('touchstart', function (){
				if(mobileFlag){
					window.location.href = linkHref;
					
				} else{
					mobileFlag = true;
					return;
				}
			});
		}else{
			itemOpener.on('click', function (){
				window.location.href = linkHref;
			});
		}
	});
	
	// handle layout resize
	ResponsiveHelper.addRange({
		'767..': {
			on: function() {
				// init startAnimation
				startAnimation (false);
			},
			off: function() {
				startAnimation (true);
			}
		}
	});
	function startAnimation (flag) {
		jQuery('ul.nav li.dropdown').each(function () {
			var itemOpener = jQuery(this).addClass('menu-item');

			var dropMenu = itemOpener.find('.dropdown-menu');
			var dropMenuHeight = dropMenu.outerHeight();
			
			if (flag) {
				itemOpener.off('mouseenter mouseleave').removeClass('menu-item').addClass('item-add');
				if (itemOpener.hasClass('item-add')){
					dropMenu.removeAttr('style');
				}
			} else {
				itemOpener.removeClass('item-add');
				itemOpener.on('mouseenter', function() {
					if (itemOpener.hasClass('menu-item')) {
						dropMenu.stop(true, true).show();
						itemOpener.css({
							'margin-bottom': dropMenuHeight
						});
					}

				});

				itemOpener.on('mouseleave', function() {
					if (itemOpener.hasClass('menu-item')) {
						dropMenu.stop(true, true).hide();
						itemOpener.removeAttr('style');
					}
				});
			}
		});
	}
}

/*
 * Responsive Layout helper
 */
ResponsiveHelper = (function($){
	// init variables
	var handlers = [],
		prevWinWidth,
		win = $(window),
		nativeMatchMedia = false;

	// detect match media support
	if(window.matchMedia) {
		if(window.Window && window.matchMedia === Window.prototype.matchMedia) {
			nativeMatchMedia = true;
		} else if(window.matchMedia.toString().indexOf('native') > -1) {
			nativeMatchMedia = true;
		}
	}

	// prepare resize handler
	function resizeHandler() {
		var winWidth = win.width();
		if(winWidth !== prevWinWidth) {
			prevWinWidth = winWidth;

			// loop through range groups
			$.each(handlers, function(index, rangeObject){
				// disable current active area if needed
				$.each(rangeObject.data, function(property, item) {
					if(item.currentActive && !matchRange(item.range[0], item.range[1])) {
						item.currentActive = false;
						if(typeof item.disableCallback === 'function') {
							item.disableCallback();
						}
					}
				});

				// enable areas that match current width
				$.each(rangeObject.data, function(property, item) {
					if(!item.currentActive && matchRange(item.range[0], item.range[1])) {
						// make callback
						item.currentActive = true;
						if(typeof item.enableCallback === 'function') {
							item.enableCallback();
						}
					}
				});
			});
		}
	}
	win.bind('load resize orientationchange', resizeHandler);

	// test range
	function matchRange(r1, r2) {
		var mediaQueryString = '';
		if(r1 > 0) {
			mediaQueryString += '(min-width: ' + r1 + 'px)';
		}
		if(r2 < Infinity) {
			mediaQueryString += (mediaQueryString ? ' and ' : '') + '(max-width: ' + r2 + 'px)';	
		}
		return matchQuery(mediaQueryString, r1, r2);
	}

	// media query function
	function matchQuery(query, r1, r2) {
		if(window.matchMedia && nativeMatchMedia) {
			return matchMedia(query).matches;
		} else if(window.styleMedia) {
			return styleMedia.matchMedium(query);
		} else if(window.media) {
			return media.matchMedium(query);
		} else {
			return prevWinWidth >= r1 && prevWinWidth <= r2;
		}
	}

	// range parser
	function parseRange(rangeStr) {
		var rangeData = rangeStr.split('..');
		var x1 = parseInt(rangeData[0], 10) || -Infinity;
		var x2 = parseInt(rangeData[1], 10) || Infinity;
		return [x1, x2].sort(function(a, b){
			return a - b;
		});
	}

	// export public functions
	return {
		addRange: function(ranges) {
			// parse data and add items to collection
			var result = {data:{}};
			$.each(ranges, function(property, data){
				result.data[property] = {
					range: parseRange(property),
					enableCallback: data.on,
					disableCallback: data.off
				};
			});
			handlers.push(result);

			// call resizeHandler to recalculate all events
			prevWinWidth = null;
			resizeHandler();
		}
	};
}(jQuery));


// logo animation start and sticky header
function initAnimation() {
	var holder = jQuery('.main-holder');
	if(jQuery('#wrapper.home').length ) {
		var holder = jQuery('.carousel');
	}else if( (jQuery('#wrapper.event-d').length) || (jQuery('#wrapper.about-us-page').length) ) {
		var holder = jQuery('.breadcrumbs');
	}

	var headerClass = 'stuck';
	var wrapperClass = 'active';
	var header = jQuery('#header');
	var wrapper = jQuery('#wrapper');
	//var holder = jQuery('.main-holder');
	var headerHeight = header.height();
	var topMargin = holder.offset().top;
	var topPosition = topMargin - headerHeight;
	function headerAnimate(){
		var top = jQuery(window).scrollTop();
		
		topPosition = topMargin - headerHeight;
		if(top >= topPosition){
			header.addClass(headerClass);
		} else{
			header.removeClass(headerClass);
		}
	}
	headerAnimate();
	
	jQuery(window).on('resize', function (){
		headerHeight = header.height();
		topMargin = holder.offset().top;
	});

		jQuery(document).on('scroll', function () {
		wrapper.addClass(wrapperClass);
		headerAnimate();
	});
}

// background Parallax effect
function initParallaxBg() {
	jQuery('.bg-holder').parallaxBG({
		parent: '.bg-frame',
		image: 'img',
		parallaxOffset: 100,
		fallbackFunc: initBackgroundResize
	});
};
(function($){
	function ParallaxBG(opt) {
		this.options = $.extend({
			parent: '.bg-frame',
			image: 'img',
			parallaxOffset: 100,
			fallbackFunc: function() {}
		}, opt);
		this.init();
	}
	ParallaxBG.prototype = {
		init: function() {
			if(this.options.holder) {
				if(typeof this.options.fallbackFunc == 'function' && (oldIe || isTouchDevice)) {
					this.options.fallbackFunc();
					return;
				}
				this.getStructure();
				this.attachEvents();
			}
		},
		getStructure: function() {
			this.holder = $(this.options.holder);
			this.parent = this.holder.find(this.options.parent);
			this.holderHeight = this.holder.height();
			this.holderOffset = this.holder.offset().top;

			// generate bg
			this.image = this.parent.find(this.options.image).eq(0).css({visibility: 'hidden'});
			this.imageRatio = this.image.attr('width')/this.image.attr('height') || this.image.width()/this.image.height();

			this.parent.css({
				backgroundImage: 'url('+this.image.attr('src')+')',
				backgroundRepeat: 'no-repeat',
				backgroundAttachment: 'fixed'
			});

			this.win = $(window);
			this.winHeight = this.win.height();
			this.winWidth = this.win.width();
			this.winScroll = this.win.scrollTop();
			this.bgHeight = this.winHeight + this.options.parallaxOffset;
		},
		attachEvents: function() {
			var self = this;
			this.bindHandlers(['scrollLayout']);
			this.bindHandlers(['resizeLayout']);
			this.win.bind('scroll', this.scrollLayout).bind('resize load', this.resizeLayout);

			// fix load problem
			setTimeout(function() {
				self.resizeLayout();
				self.win.trigger('scroll');
			}, 500);
		},
		resizeLayout: function() {
			// get dimensions
			this.winHeight = this.win.height();
			this.winWidth = this.win.width();
			this.holderHeight = this.holder.height();
			this.holderOffset = this.holder.offset().top;
			
			this.blockHeight = this.winHeight + this.options.parallaxOffset;
			this.currentTop = Math.max(0, this.blockHeight - this.holderHeight);

			// get parallax ratio and image ratio state
			this.parallaxRatio = this.win.width()/(this.winHeight + this.options.parallaxOffset);
			this.ratioState = this.imageRatio <= this.parallaxRatio;
			
			if(this.ratioState) {
				this.bgWidth = this.winWidth;
				this.bgHeight = this.bgWidth/this.imageRatio;
			} else {
				this.bgWidth = 'auto';
				this.bgHeight = this.winHeight + this.options.parallaxOffset;
			}

			this.parent.css({
				paddingBottom: this.currentTop,
				backgroundSize: this.bgWidth != 'auto' ? this.bgWidth +'px '+this.bgHeight+'px' : this.bgWidth +' '+this.bgHeight+'px'
			});
			this.scrollLayout();
		},
		scrollLayout: function() {
			this.winScroll = this.win.scrollTop();
			this.offsetPercentage = Math.max(0, Math.min((this.winScroll + this.winHeight - this.holderOffset)/(this.winHeight + this.holderHeight), 1)).toFixed(4);

			if(!!this.ratioState) {
				var curPos = '50% ' + ((-parseFloat(this.offsetPercentage)*this.options.parallaxOffset) - (this.bgHeight - this.winHeight)/2)+'px';
			} else {
				var curPos = '50% ' + (-parseFloat(this.offsetPercentage)*this.options.parallaxOffset)+'px';
			}

			this.parent.css({backgroundPosition: curPos});
		},
		bindHandlers: function(handlersList) {
			var self = this;
			$.each(handlersList, function(index, handler) {
				var origHandler = self[handler];
				self[handler] = function() {
					return origHandler.apply(self, arguments);
				};
			});
		}
	};

	// detect device type
	var isTouchDevice = /MSIE 10.*Touch/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	var oldIe = window.attachEvent && !window.addEventListener;

	// jquery parallax plugin
	$.fn.parallaxBG = function(opt){
		return this.each(function(){
			new ParallaxBG($.extend(opt,{holder:this}));
		});
	};
}(jQuery));

